import React, { useState } from "react";
import SpeechRecognition, {
  useSpeechRecognition,
} from "react-speech-recognition";
import { askLlama, storeInterview } from "./api"; // Import the API methods
import "./App.css"; // Import CSS for styling

function App() {
  const [name, setName] = useState("");
  const [questions, setQuestions] = useState([]);
  const [currentQuestion, setCurrentQuestion] = useState("");
  const [currentAnswer, setCurrentAnswer] = useState("");
  const { transcript, resetTranscript } = useSpeechRecognition();
  const [llamaResponse, setLlamaResponse] = useState("");

  // Function to fetch a question from the LLM (LLama) backend
  const fetchLlamaQuestion = async () => {
    try {
      const response = await askLlama("Ask me a question about programming");
      setCurrentQuestion(response.data.question);
      setQuestions([...questions, response.data.question]);
      setLlamaResponse(response.data.response); // Store the full response
    } catch (error) {
      console.error("Error fetching question from LLama:", error);
      setLlamaResponse("Error: Unable to fetch question from LLama");
    }
  };

  // Function to handle speech input
  const handleStartListening = () => {
    SpeechRecognition.startListening({ continuous: true });
  };

  const handleStopListening = () => {
    SpeechRecognition.stopListening();
    setCurrentAnswer(transcript); // Set the current transcript as the answer
    resetTranscript();
  };

  // Function to submit the interview responses to the backend
  const handleSubmit = async () => {
    const interview = {
      interviewee_name: name,
      responses: questions.map((q) => ({ question: q, answer: currentAnswer })),
    };
    console.log("Submitting interview data:", interview);
    try {
      await storeInterview(interview); // This will call the backend and store the interview
      alert("Interview submitted!");
    } catch (error) {
      console.error("Error submitting the interview:", error);
    }
  };

  return (
    <div className="App">
      <h1>Interview Bot</h1>

      <input
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />

      <button onClick={fetchLlamaQuestion}>Ask LLama</button>

      {llamaResponse && (
        <div>
          <h3>LLama Response:</h3>
          <p>{llamaResponse}</p>
        </div>
      )}

      <p>
        <strong>Question:</strong> {currentQuestion}
      </p>

      <button onClick={handleStartListening}>Start Answering</button>
      <button onClick={handleStopListening}>Stop Answering</button>

      <p>
        <strong>Your Answer:</strong> {transcript}
      </p>

      <button onClick={handleSubmit}>Submit Interview</button>
    </div>
  );
}

export default App;
